create table dbo.ad_categories (category_uuid binary(16) not null, category_name varchar(255), parent binary(16), primary key (category_uuid));
create table dbo.ad_photos (photo_uuid binary(16) not null, created_by varchar(255) not null, created_date datetime2 not null, last_modified_by varchar(255) not null, last_modified_date datetime2 not null, image_byte varbinary(max) not null, photo_name varchar(255) not null, primary_ind bit not null, advertisement_uuid binary(16) not null, primary key (photo_uuid));
create table dbo.advertisements (advertisement_uuid binary(16) not null, created_by varchar(255) not null, created_date datetime2 not null, last_modified_by varchar(255) not null, last_modified_date datetime2 not null, ad_location varchar(255), description varchar(255) not null, num_views integer, price decimal(19,2) not null, status varchar(1) not null, summary varchar(255), title varchar(255) not null, category_uuid binary(16) not null, seller_uuid binary(16) not null, primary key (advertisement_uuid));
create table dbo.seller (seller_uuid binary(16) not null, created_by varchar(255) not null, created_date datetime2 not null, last_modified_by varchar(255) not null, last_modified_date datetime2 not null, activation_code varchar(6) not null, cell_phone varchar(12), email varchar(255), first_name varchar(255), home_phone varchar(12), last_name varchar(255), secret varchar(255) not null, status varchar(1) not null, primary key (seller_uuid));
