create table ad_categories (category_uuid binary not null default random_uuid(), category_name varchar(255), parent binary, primary key (category_uuid));
create table ad_photos (photo_uuid binary not null, created_by varchar(255) not null, created_date timestamp not null, last_modified_by varchar(255) not null, last_modified_date timestamp not null, image_byte blob not null, photo_name varchar(255) not null, primary_ind boolean, advertisement_uuid binary not null, primary key (photo_uuid));
create table advertisements (advertisement_uuid binary not null, created_by varchar(255) not null, created_date timestamp not null, last_modified_by varchar(255) not null, last_modified_date timestamp not null, ad_location varchar(255), description varchar(255) not null, num_views integer, price decimal(19,2) not null, status varchar(1) not null, summary varchar(255), title varchar(255) not null, category_uuid binary not null, seller_uuid binary not null, primary key (advertisement_uuid));
create table seller (seller_uuid binary not null, created_by varchar(255) not null, created_date timestamp not null, last_modified_by varchar(255) not null, last_modified_date timestamp not null, activation_code varchar(6) not null, cell_phone varchar(12), email varchar(255), first_name varchar(255), home_phone varchar(12), last_name varchar(255), secret varchar(255) not null, status varchar(1) not null, primary key (seller_uuid));
